                               </tr>\n                                        </table>\n                                        <!-- // FLEXIBLE CONTAINER -->\n                                    </td>\n                                </tr>\n                            </table>\n                            <!-- // CENTERING TABLE -->\n                        </td>\n                    </tr>\n                    <!-- // END -->\n\n                </table>\n\n                <table bgcolor=\"#FFFFFF\"  border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"500\" id=\"emailBody\">\n\n\n                    <tr>\n                        <td align=\"center\" valign=\"top\">\n                            <!-- CENTERING TABLE // -->\n\n                            <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\" style=\"color:#FFFFFF;\" bgcolor=\"#cc0033\">\n                                <tr>\n                                    <td align=\"center\" valign=\"top\">\n\n                                        <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"500\" class=\"flexibleContainer\">\n                                            <tr>\n                                                <td align=\"center\" valign=\"top\" width=\"500\" class=\"flexibleContainerCell\">\n\n                                                    <table border=\"0\" cellpadding=\"30\" cellspacing=\"0\" width=\"100%\">\n                                                        <tr>\n                                                            <td align=\"center\" valign=\"top\" class=\"textContent\">\n                                                                <h1 style=\"color:#FFFFFF;line-height:100%;font-family:Helvetica,Arial,sans-serif;font-size:35px;font-weight:normal;margin-bottom:5px;text-align:center;\">Submission Details</h1>\n                                                                <h2 style=\"text-align:center;font-weight:normal;font-family: