or=\"#E1E1E1\">\n\n                                                                <div style=\"font-family:Helvetica,Arial,sans-serif;font-size:13px;color:#828282;text-align:center;line-height:120%;\">\n                                                                    <div>Copyright &#169; 2016 - 2017 <a href=\"www.sdsu.edu\" target=\"_blank\" style=\"text-decoration:none;color:#828282;\"><span style=\"color:#828282;\">San Diego State University</span></a>. All&nbsp;rights&nbsp;reserved.</div>\n\n                                                                </div>\n\n                                                            </td>\n                                                        </tr>\n                                                    </table>\n                                                </td>\n                                            </tr>\n                                        </table>\n                                        <!-- // FLEXIBLE CONTAINER -->\n                                    </td>\n                                </tr>\n                            </table>\n                            <!-- // CENTERING TABLE -->\n                        </td>\n                    </tr>\n\n                </table>\n                <!-- // END -->\n\n            </td>\n        </tr>\n    </table>\n</center>\n</body>\n</html>', 'Y', '2018-04-11 05:06:59', 'SDSU-CMS-1', 'SDSU-CMS', 'N', 'SDSU-CMS', 'MESSAGE', 'N', 'email', 'SDSU-CMS ADMIN');
